#pragma once

#include "../IAppGateway.h"

// Minimal JSON-RPC stub wrapper for AppGatewayResolver.
// Upstream entservices-infra/AppGateway (develop) uses Exchange::JAppGatewayResolver
// for JSONRPC registration. In a full build this is auto-generated by Thunder's
// JsonGenerator, but in this repository we provide a lightweight stub so the
// plugin can build and tests can link.
//
// The Register/Unregister functions are no-ops here; runtime JSON-RPC exposure
// is not required for L0 unit tests that focus on core behavior.

namespace WPEFramework {
namespace PluginHost {
class JSONRPC;
}
namespace Exchange {

struct JAppGatewayResolver {
    static void Register(PluginHost::JSONRPC&, IAppGatewayResolver*) {}
    static void Unregister(PluginHost::JSONRPC&) {}
};

} // namespace Exchange
} // namespace WPEFramework
